mapper.select:
SELECT ask.id as qId, ask.title,ask.type,ask.tag,ask.content,ask.collect,ask.readcount,ask.hotnum,ask.price,ask.good,ask.gmt_create,user.nickname, (select count(reply.id) as replaycount from red_ask_reply reply where reply.qid=ask.id) as reply FROM edu_user_ask ask LEFT OUTER JOIN edu_user user ON ask.uid=user.id
where qustype = #{qustype}
ORDER BY ask.gmt_create DESC limit 20

SELECT ask.id as qId, ask.title,ask.type,ask.tag,ask.content,ask.collect,ask.readcount,ask.hotnum,ask.price,ask.good,ask.gmt_create,user.nickname, (select count(reply.id) from red_ask_reply reply where reply.qid=ask.id) as reply FROM edu_user_ask ask LEFT OUTER JOIN edu_user user ON ask.uid=user.id
where qustype = #{qustype}
HAVING reply>0 ORDER BY ask.gmt_create DESC limit 20

SELECT ask.id as qId, ask.title,ask.type,ask.tag,ask.content,ask.collect,ask.readcount,ask.hotnum,ask.price,ask.good,ask.gmt_create,user.nickname, (select count(reply.id) from red_ask_reply reply where reply.qid=ask.id) as reply FROM edu_user_ask ask LEFT OUTER JOIN edu_user user ON ask.uid=user.id
where qustype = #{qustype}
HAVING reply=0 ORDER BY ask.gmt_create DESC limit 20

SELECT ask.id as qId, ask.title,ask.type,ask.tag,ask.content,ask.collect,ask.readcount,ask.hotnum,ask.price,ask.good,ask.gmt_create,user.nickname, (select count(reply.id) from red_ask_reply reply where reply.qid=ask.id) as reply FROM edu_user_ask ask LEFT OUTER JOIN edu_user user ON ask.uid=user.id
where qustype = #{qustype}
ORDER BY reply DESC limit 20

SELECT ask.id as qId, ask.title,ask.type,ask.tag,ask.content,ask.collect,ask.readcount,ask.hotnum,ask.price,ask.good,ask.gmt_create,user.nickname, (select count(reply.id) from red_ask_reply reply where reply.qid=ask.id) as reply FROM edu_user_ask ask LEFT OUTER JOIN edu_user user ON ask.uid=user.id
where qustype = #{qustype}
ORDER BY ask.readcount DESC limit 20

SELECT ask.id as qId, ask.title,ask.type, ask.price, ask.tag,ask.content,ask.collect,ask.readcount,ask.hotnum,ask.price,ask.good,ask.gmt_create,user.nickname, (select count(reply.id) from red_ask_reply reply where reply.qid=ask.id) as reply FROM edu_user_ask ask LEFT OUTER JOIN edu_user user ON ask.uid=user.id where price>0
and qustype = #{qustype}
ORDER BY ask.gmt_create DESC limit 20

mapper.update:
update edu_user_ask
set readcount = #{readCount}
where id = #{id}

update edu_user_ask
set good=good+1
where id = #{qid}

update edu_user_ask
set good=good-1
where id = #{qid}

update edu_user_ask
set collect=collect+1
where id = #{qid}

update edu_user_ask
set collect=collect-1
where id = #{qid}
