mapper.select:
SELECT qustion.id as qId, qustion.title,qustion.type,qustion.content,qustion.deep,qustion.collect,qustion.readcount,qustion.hotnum,qustion.price,qustion.good,(tmeet+smeet+pmeet) as meet,qustion.gmt_create,user.nickname, qustion.uid,(pmeet+tmeet+smeet)/ (readcount + 0.0 ) as frequency,(select count(reply.id) as replaycount from red_interview_answer reply where reply.qid=qustion.id) as reply ,tag.id as ctid,tag.name as tname,tag.img as timg,tag.sort as tsort FROM red_interview_question qustion LEFT OUTER JOIN edu_user user ON qustion.uid=user.id LEFT OUTER JOIN red_interview_tags tags ON tags.qid = qustion.id LEFT OUTER JOIN red_category_tag tag ON tag.id = tags.tid
where tag.path = #{tag}
where qustype = #{qustype}
and qId != #{qid}
ORDER BY qustion.hotnum
ORDER BY qustion.gmt_create
ORDER BY qustion.readcount
ORDER BY reply
DESC limit #{size}

SELECT qustion.id as qId, qustion.title,qustion.type,qustion.content,qustion.deep,qustion.collect,qustion.readcount,qustion.hotnum,qustion.price,qustion.good,(tmeet+smeet+pmeet) as meet,qustion.gmt_create,user.nickname, qustion.uid,(select count(reply.id) as replaycount from red_interview_answer reply where reply.qid=qustion.id) as reply ,tag.id as ctid,tag.name as tname,tag.img as timg,tag.sort as tsort FROM red_interview_question qustion LEFT OUTER JOIN edu_user user ON qustion.uid=user.id LEFT OUTER JOIN red_interview_tags tags ON tags.qid = qustion.id LEFT OUTER JOIN red_category_tag tag ON tag.id = tags.tid LEFT OUTER JOIN red_interview_question_position tposition ON tposition.qid = qustion.id where tposition.pid = #{pid}
ORDER BY qustion.hotnum
ORDER BY ddegree
ORDER BY reply
ORDER BY frequency
DESC limit #{size}

mapper.update:
update red_interview_question
set readcount = #{readCount}
where id = #{id}

update red_interview_question
set good=good+1
where id = #{qid}

update red_interview_question
set good=good-1
where id = #{qid}

update red_interview_question
set collect=collect+1
where id = #{qid}

update red_interview_question
set collect=collect-1
where id = #{qid}

mapper.select:
SELECT !isnull(collect.id) as isCollect, !isnull(good.id) as goodslect,!isnull(interview.id) as isFocus
FROM red_interview_question interview
LEFT OUTER JOIN  red_interview_collect collect on collect.uid = #{uid} and collect.qid = #{qid} and collect.is_deleted = 0
LEFT OUTER JOIN  red_user_focus focus on focus.uid = #{uid} and focus.fid = interview.uid and focus.is_deleted = 0
LEFT OUTER JOIN  red_interview_good  good  on good.uid = #{uid} and good.qid = #{qid}
where interview.id = #{qid}

mapper.update:
update red_interview_question
set state = #{state}
where id = #{qid} and uid = #{uid}

update red_interview_question
set smeet=smeet+1
where id = #{qid}

update red_interview_question
set tmeet=tmeet+1
where id = #{qid}

update red_interview_question
set pmeet=pmeet+1
where id = #{qid}

update red_interview_question
set umeet=umeet+1
where id = #{qid}
