mapper.select:
SELECT eduser.*,(SELECT COUNT (reply.id)
  FROM  red_ask_reply reply
  WHERE reply.uid = #{uid} and reply.state = 1
) as aDopt,(SELECT COUNT (reply.id)
  FROM  red_ask_reply reply
  WHERE reply.uid = #{uid}
) as qCount,(SELECT COUNT (reply.id)
  FROM  red_ask_reply reply
  WHERE reply.uid = #{uid}
) as qCount,
FROM edu_user eduser
where eduser.id = #{uid}

mapper.update:
update edu_user
set

    ${key} =  #{val}

where id = #{id} and password = #{oldPwd}
